#include "sock.h"#include "excp.h"#include <unistd.h>Socket::Socket(int sd){    if(sd >= 0)        _sd = sd;    else if((_sd = socket(AF_INET, SOCK_STREAM, 0)) >= 0)        ;    else         throw SocketException("Не удалось создать сокет");}Socket::~Socket(){    if(_sd >= 0)    {        shutdown(_sd, SHUT_RDWR);        close(_sd);    }}SocketAddress::SocketAddress(const string hostname, short port){	_addr.sin_family = AF_INET;	_addr.sin_port = htons(port);	inet_aton(hostname.c_str(),&_addr.sin_addr);}